css:
.card {
  font-family: Arial;
  font-size: 20px;
  text-align: left;
  color: black;
  background-color: white;
  max-width: 95%;
  margin: 0 auto;
  padding: 15px;
  position: relative;
}

.card-type-indicator {
  position: absolute;
  top: 5px;
  right: 5px;
  background: #4CAF50;
  color: white;
  padding: 2px 8px;
  border-radius: 10px;
  font-size: 0.8em;
}

/* 响应式布局 */
.content-wrapper {
  display: flex;
  flex-direction: column;
}

@media screen and (min-width: 768px) and (orientation: landscape) {
  .content-wrapper {
    flex-direction: row;
    align-items: flex-start;
  }
  .text-content {
    flex: 1;
    padding-right: 15px;
  }
  .image-content {
    flex: 1;
  }
}

/* 单词卡片样式 */
.section1, .section2 {
  margin-bottom: 20px;
  padding: 15px;
  border-radius: 8px;
  background-color: #f8f9fa;
}

.unit {
  color: #666;
  font-size: 0.9em;
  margin-bottom: 5px;
}

.word, .word2 {
  font-size: 2em;
  font-weight: bold;
  color: #2c3e50;
  margin-bottom: 10px;
}

.symbol {
  color: #888;
  font-style: italic;
  margin-bottom: 10px;
}

.audio1, .audio2 {
  margin-top: 10px;
}

.mnemonic-section {
  border-left: 3px solid #4CAF50;
  padding-left: 10px;
  margin: 10px 0;
}

.collocations {
  color: #2196F3;
  font-weight: bold;
}

.chn {
  margin-bottom: 10px;
  font-size: 1.1em;
}

.difficulty, .memory_tips, .word_family, .extra7 {
  margin-bottom: 5px;
  color: #555;
}

.example_en, .example_cn {
  margin-bottom: 5px;
  padding: 8px;
  background-color: #f0f0f0;
  border-radius: 5px;
}

.ssound1 {
  margin-bottom: 10px;
}

.photo, .photo2 {
  margin-top: 15px;
}

.photo img, .photo2 img, .image-content img {
  max-width: 100%;
  height: auto;
  border-radius: 5px;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

/* 背诵卡片样式 */
.text-segment {
  margin: 10px 0;
  padding: 10px;
  background-color: #f8f9fa;
  border-radius: 5px;
  line-height: 1.6;
}

.segment {
  margin: 10px 0;
  padding: 10px;
  border-radius: 5px;
  background-color: #f8f9fa;
  transition: background-color 0.3s;
}

.hint {
  font-weight: bold;
  color: #2c3e50;
  margin-bottom: 5px;
}

.translation {
  margin-top: 15px;
  padding: 10px;
  background-color: #e9f7e9;
  border-radius: 5px;
  line-height: 1.6;
}

.full-text {
  white-space: pre-line;
  margin-bottom: 15px;
  line-height: 1.6;
}

button {
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 5px 10px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 5px;
  font-size: 0.9em;
}

button:hover {
  background-color: #45a049;
}

/* 夜间模式适配 */
.nightMode .card {
  background-color: #1e1e1e;
  color: #e0e0e0;
}

.nightMode .section1, .nightMode .section2, .nightMode .segment {
  background-color: #2d2d2d;
}

.nightMode .word, .nightMode .word2 {
  color: #ffffff;
}

.nightMode .hint {
  color: #bbbbbb;
}

.nightMode .translation {
  background-color: #2d3d2d;
}

.nightMode .example_en, .nightMode .example_cn {
  background-color: #333333;
}
